-- Database: spk_smart_mcc
CREATE DATABASE IF NOT EXISTS spk_smart_mcc;
USE spk_smart_mcc;

-- Tabel Kriteria
CREATE TABLE kriteria (
    id_kriteria VARCHAR(10) PRIMARY KEY,
    nama_kriteria VARCHAR(100) NOT NULL,
    jenis_kriteria ENUM('benefit', 'cost') NOT NULL,
    satuan VARCHAR(20),
    deskripsi TEXT
);

-- Insert data kriteria

-- Tabel Alternatif (SMD)
CREATE TABLE alternatif (
    id_alternatif VARCHAR(10) PRIMARY KEY,
    nama_alternatif VARCHAR(50) NOT NULL,
    deskripsi TEXT
);

-- Insert data alternatif

-- Tabel Nilai Alternatif
CREATE TABLE nilai_alternatif (
    id INT AUTO_INCREMENT PRIMARY KEY,
    id_alternatif VARCHAR(10),
    id_kriteria VARCHAR(10),
    nilai DECIMAL(10,2),
    FOREIGN KEY (id_alternatif) REFERENCES alternatif(id_alternatif),
    FOREIGN KEY (id_kriteria) REFERENCES kriteria(id_kriteria)
);

-- Insert data nilai alternatif berdasarkan tabel dari artikel

-- Tabel Bobot Kriteria
CREATE TABLE bobot_kriteria (
    id INT AUTO_INCREMENT PRIMARY KEY,
    id_kriteria VARCHAR(10),
    bobot DECIMAL(10,6),
    metode_pembobotan ENUM('manual', 'roc') DEFAULT 'manual',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (id_kriteria) REFERENCES kriteria(id_kriteria)
);

-- Tabel Hasil Perhitungan SMART
CREATE TABLE hasil_smart (
    id INT AUTO_INCREMENT PRIMARY KEY,
    id_alternatif VARCHAR(10),
    skor_akhir DECIMAL(10,6),
    ranking INT,
    metode_pembobotan ENUM('manual', 'roc'),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (id_alternatif) REFERENCES alternatif(id_alternatif)
);
